//Functions
function inputFocus(target) {
	var input = target;
	for (var a=input.length-1; a >= 0; a--) {
		if (input[a].type != 'text') { continue; } // skips non-text inputs
		input[a].mask = input[a].value;
		input[a].onfocus = clearInput;
		input[a].onblur = fillInput;
	}
	function clearInput() {
		if (this.value == this.mask) { this.value = ''; }
	}
	function fillInput() {
		if (this.value === '') { this.value = this.mask; }
	}
}



// Responsive Modal Functions
// ===============================
// Globals
var startWidth;
var thisHref;
var thisTitle;
// Display sign up form in modal or slides the form open
function responsiveModal(target, title) {
	thisHref = target;
	thisTitle = title;
	startWidth = binaryMobile();
	$('.hideMe').hide();
	$('.hideMe').has(thisHref).prependTo('#contentContainer');
	if (startWidth == "mobile") {
		$.scrollTo($('#contentContainer'), 500, {
			onAfter:function(){
				$(thisHref).parents('.hideMe').slideDown(1000, toggleModal);
		}
		});
	} else {
		$.fancybox({
			scrolling: 'no',
			onComplete:toggleModal,
			onClosed: function() {
                $('body').removeClass('toggleModal');
                MyMove.onModalClose(thisHref, thisTitle)
                if (typeof HandleChecklistSaveOperationCancellation == 'function') {
                    HandleChecklistSaveOperationCancellation();
                }
            },
			href: thisHref,
			title: thisTitle,
			titlePosition: 'inside'/*
,
			autoScale:false,
			autoDimensions:false
*/
		});
	}
}

// Function toggles transition if user resizes browser after activating the form
function toggleModal() {
	$("input:checkbox, input:radio").each(function(){
		if(!$(this).parents().hasClass('hideMe') && !$(this).hasClass('opt-in-checkbox') && !$(this).parents().hasClass('nojqtransform')) {
			$(this).jqTransCheckBox();
		}
	});
	$('body').addClass('toggleModal');
	$(thisHref).find('input.start').focus();
	$(window).unbind('resize', modalResize);
	$(window).bind('resize', modalResize);
	
	MyMove.onModalOpen(thisHref, thisTitle);
}


function modalResize() {
	if ($('body').hasClass('toggleModal')) {
		var newWidth = binaryMobile();
		if(newWidth != startWidth) {
			if(newWidth == "mobile") {
				parent.$.fancybox.close();
				$.scrollTo($('#contentContainer'), 500, {
					onAfter:function(){
						$(thisHref).parents('.hideMe').slideDown(1000);
						$('body').addClass('toggleModal');
					}
				});
				startWidth = "mobile";
			} else {
				$(thisHref).parents('.hideMe').hide();
				$.fancybox({
					scrolling: 'no',
					onStart: toggleModal,
					onClosed: function() {
                        $('body').removeClass('toggleModal');
                    },
					href: thisHref,
					title: thisTitle,
					titlePosition: 'inner'
				});
				startWidth = "notMobile";
			}
		}
	}
}


// Password replace functionality so that label displays in input
// ===============================

function passwordSwap() {
	$('.swapblankinputwithlabel').each(function() {
		var input = $(this);
		//wrap the input
		input.wrap('<div class="pswd-wrapper" />');
		var wrapper = input.parents('.pswd-wrapper');
		wrapper.width(input.width());
		//create new text input
		$("<div>").text(input.parent().prev('label').text())

		//.addClass(input.attr("class"))
		.addClass('pswd-overlay')
		.prependTo(wrapper)
		.click(function(){
			$(this).hide();
			input.show();
			input.focus();
		});

		input.focus(function(){
			input.prev('.pswd-overlay').hide();
		});
		
		//working in conjunction with $.fn.listenForChange plugin
		input.change(function(){
		    if($(this).val() !== input.prev('.pswd-overlay').text() && $(this).val() !== '') {
		        input.prev('.pswd-overlay').hide();
            }
        })

		// If actual password is empty switch back to input[type=text]
		input.blur(function(){
			if($(this).val() === '') {
				input.prev('.pswd-overlay').show();
			}
		});
	});
}


