// "Media queries" that correspond with css
function jmediaQuery() {
	var width = $(document).width();
	var result;
	if (width <= 767) {
		result = "mobile";
	} else if ((width >= 768) && (width <=1024)) {
		result = "tablet";
	} else {
		result = "desktop";
	}
	return result;
}

// Either it's mobile or it ain't
function binaryMobile() {
	if (jmediaQuery() == "mobile") {
		return "mobile";
	} else {
		return "notMobile";
	}
}
